import React, { useState } from "react";
import { Button, Card, Form } from "react-bootstrap";
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';





const BookingCard = ({ booking, date }) => {

  const [open, setOpen] = useState(false);
 
  const onOpenModal = () => setOpen(true);
  const onCloseModal = () =>
 
  setOpen(false);
 

  return (
    <div>
      
      <Card style={{width: '18rem', margin:"1rem", alignItems:"center"}}>
        <Card.Body>
          <Card.Title>{booking.subject}</Card.Title>
          <Card.Text>
            {booking.time}
          </Card.Text>
          <Card.Text>
          {booking.date}
          </Card.Text>
          <Card.Text>
          {booking.visitingHour}
          </Card.Text>
          <Card.Text>
          Total space available {booking.totalSpace}
          </Card.Text>
          <Button onClick={onOpenModal} variant="primary">Book Now</Button>
          
          
          
          <Modal open={open} center>
            <h3>Please provide your necessary information</h3>
          <div style={{ backgroundColor: "#3A4256", color:"white", padding:"1rem"}}>
      <Form className="container" style={{width:"30rem", alignItems:"centre"}}>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" />
        </Form.Group>
        <div className="d-flex">
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Name</Form.Label>
          <Form.Control type="text" placeholder="Enter your Name.." />
        </Form.Group>
        <Form.Group style={{marginLeft:"10px"}} controlId="formBasicEmail"> 
          <Form.Label>Phone Number</Form.Label>
          <Form.Control type="text" placeholder="Enter your phone number" />
        </Form.Group>
        </div>

        <Form.Group controlId="formBasicPassword">
          <Form.Label>Address</Form.Label>
          <Form.Control style={{height:"10rem"}} type="text" placeholder="Please type your address.." />
        </Form.Group>
        <Form.Group controlId="formBasicCheckbox"></Form.Group>
        <Button onClick={onCloseModal} variant="primary">
          Submit
        </Button>
      </Form>
    </div>
        
      </Modal>
        </Card.Body>
      </Card>
     
    </div>
  );
};

export default BookingCard;


