import React from "react";
import "react-responsive-modal/styles.css";
import { Modal } from "react-responsive-modal";
import { useForm } from "react-hook-form";
import { Button } from "react-bootstrap";


const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)'
  }
};

Modal.setAppElement('#yourAppElement')


const AppointmentFrom = ({ onCloseModals, opens, onOpenModals}) => {

  var subtitle;
  const [modalIsOpen,setIsOpen] = React.useState(false);
  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = '#f00';
  }

  function closeModal(){
    setIsOpen(false);
  }

    const { register, handleSubmit,  errors } = useForm();

    const onSubmit = data => {

        fetch("http://localhost:5000/userAppointment", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data)
      })
        .then((res) => res.json())
        .then(success => {
          if (success) {
            onCloseModals();
            alert("Appointment created successfully!");
          }
        })
    }

  return (
    <div>
      <Modal open={opens}>
        <h3>Please provide your necessary information</h3>
        <div 
          style={{
            backgroundColor: "#3A4256",
            color: "white",
            padding: "1rem",
          }}
        >

    <div className="d-flex">
    <form  onSubmit={handleSubmit(onSubmit)}>

    <input name="example"  ref={register} />


    <input name="exampleRequired" ref={register({ required: true })} />

    {errors.exampleRequired && <span>This field is required</span>}

    <input type="submit">Submit</input>
  </form>
    </div>


        </div>
      </Modal>
    </div>
  );
};

export default AppointmentFrom;
