import React from 'react';
import { Card } from 'react-bootstrap';
import img3 from"../../../../images/Ellipse 3.png";
import img2 from"../../../../images/Ellipse 2.png";
import img1 from"../../../../images/Ellipse 1.png";
const clients = [
    {
        name:"Al Amin",
        profession:"Army",
        img:{img1},
        text:"Lorem ipsum dolor sit amet consectetur adipisicing elit. Totam dolor tenetur illum, officiis omnis voluptate dolores et a voluptates corporis?"
    },
    {
        name:"Fahim",
        profession:"Army",
        img:{img2},
        text:"Lorem ipsum dolor sit amet consectetur adipisicing elit. Totam dolor tenetur illum, officiis omnis voluptate dolores et a voluptates corporis?"
    },
    {
        name:"Sakib",
        profession:"Army",
        img:{img3},
        text:"Lorem ipsum dolor sit amet consectetur adipisicing elit. Totam dolor tenetur illum, officiis omnis voluptate dolores et a voluptates corporis?"
    }
];

const Testimonial = () => {
    return (
        <div>
            <div>
                <h1>Testimonial</h1>
                <h2>What our clients say about our services!</h2>

            </div>
            <div className="d-flex p-2 justify-content-center">
                {
                    clients.map((client) => <Card style={{width:"18rem", margin:"1em", padding:"0.5rem"}}>
                        <p>{client.text} </p>
                        <div className="d-flex">
                            <img src={client.img} alt=""/>
                            <p>{client.profession}</p>
                        </div>
                   
                        </Card>)
                }
            </div>
        </div>
    );
};

export default Testimonial;
