import React, { useState } from 'react'
import { Button, Form } from 'react-bootstrap'
import Sidebar from '../Dashboard/Sidebar/Sidebar'

const AddDoctor = () => {
  const [newInfo, setNewInfo] = useState({})
  const [files, setFiles] = useState(null)

  const handleBlur = (e) => {
    const newData = { ...newInfo }
    newData[e.target.name] = [e.target.value]
    setNewInfo(newData);
    console.log(newData);
  }

  const handleFiles = (e) => {
      const newFiles = e.target.files[0];
      setFiles(newFiles);
  }

  const handleSubmit = () => {
      
  }

  return (
    <div className="row container-fluid d-flex justify-content-center">
      <div className="col-md-3 mt-5 ml-5 col-sm-6">
        <Sidebar />
      </div>
      <div className="col-md-8 mt-5 col-sm-12">
        <Form>
          <Form.Group controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control onBlur={handleBlur} type="email" placeholder="Enter email" />
          </Form.Group>
          <Form.Group controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control onBlur={handleBlur} type="password" placeholder="Password" />
          </Form.Group>
          <Form.Group controlId="formBasicCheckbox">
            <Form.Check onChange={handleFiles} type="file" />
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </div>
    </div>
  )
}

export default AddDoctor
