import React, { useState } from 'react';
import { Button, Card, Form} from 'react-bootstrap';

// const clients = [
//     {
//         name:"Al Amin",
//         profession:"Army",
//         img:`https://ibb.co/1nxypSb`,
//         text:"Lorem ipsum dolor sit amet consectetur adipisicing elit. Totam dolor tenetur illum, officiis omnis voluptate dolores et a voluptates corporis?"
//     },
//     {
//         name:"Fahim",
//         profession:"Army",
//         img:`https://ibb.co/1nxypSb`,
//         text:"Lorem ipsum dolor sit amet consectetur adipisicing elit. Totam dolor tenetur illum, officiis omnis voluptate dolores et a voluptates corporis?"
//     },
//     {
//         name:"Sakib",
//         profession:"Army",
//         img:`https://ibb.co/gvGtSSw`,
//         text:"Lorem ipsum dolor sit amet consectetur adipisicing elit. Totam dolor tenetur illum, officiis omnis voluptate dolores et a voluptates corporis?"
//     }
// ];

const Testimonial = () => {
const [review, setReview] = useState({});

  const handleBlur = (e) => {
    const newData = { ...review}
    newData[e.target.name] = [e.target.value]
    setReview(newData);
    console.log(newData);
  }
    const handleReview = data => {
         fetch('http://localhost:5001/review', {
        method: 'POST',
        headers: { 'content-type': 'application/json' },
        body: JSON.stringify(data)
      })
      .then(res => res.json())
      .then(datas => setReview(datas));
    }
    return (
        <div className="container mt-4">
            <div style={{textAlign:"center"}}>
                <h1>Testimonial</h1> <br/>
                <h2>What our clients say about our services!</h2>

            </div>
            <div className="d-flex p-1 justify-content-center">
                {/* {
                    review.map((client) => 
                    <Card style={{width: '18rem', margin:"1rem"}}>
                    <Card.Body>
                      <Card.Text>
                       {client.text}
                      </Card.Text>
                      <Card.Text>
                       {client.profession}
                      </Card.Text>
                    </Card.Body>
                    <Card.Img style={{height:"10px", width:"10px"}} 
                    src={client.img} rounded/>
                  </Card>)
                } */}

<Form onSubmit={handleReview}>
  <Form.Group controlId="formBasicEmail">
    <Form.Label>Email address</Form.Label>
    <Form.Control onBlur={handleBlur} type="email" placeholder="Enter email" />
    
  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label>Review</Form.Label>
    <Form.Control onBlur={handleBlur} type="text" placeholder="Give your review" />
  </Form.Group>
  
  <Button variant="primary" type="submit">
    Submit
  </Button>
</Form>
            </div>
        </div>
    );
};

export default Testimonial;
