import React from "react";
import { useState } from "react";
import { Button, Card, Form } from "react-bootstrap";
import "react-responsive-modal/styles.css";
import { Modal } from "react-responsive-modal";

const AppointmentFrom = ({ onCloseModal, open,onOpenModal  }) => {
  const handleSubmit = (data) => {
    const onSubmit = () => {
      console.log(data);

      fetch(`http://localhost:5000/addAppointment`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      })
        .then((res) => res.json())
        .then((success) => {
          if (success) {
            onCloseModal();
            alert("Appointment created successfully!");
          }
        });
    };
  };

  return (
    <div>
      <Modal open={open} center>
        <h3>Please provide your necessary information</h3>
        <div
          style={{
            backgroundColor: "#3A4256",
            color: "white",
            padding: "1rem",
          }}
        >
          <Form
            onSubmit={handleSubmit()}
            className="container"
            style={{ width: "30rem", alignItems: "centre" }}
          >
            <Form.Group controlId="formBasicEmail">
              <Form.Label>Email address</Form.Label>
              <Form.Control type="email" placeholder="Enter email" required />
            </Form.Group>
            <div className="d-flex">
              <Form.Group controlId="formBasicEmail" required>
                <Form.Label>Name</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter your Name.."
                  required
                />
              </Form.Group>
              <Form.Group
                style={{ marginLeft: "10px" }}
                controlId="formBasicEmail"
                required
              >
                <Form.Label>Phone Number</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter your phone number"
                />
              </Form.Group>
            </div>

            <Form.Group controlId="formBasicPassword" required>
              <Form.Label>Address</Form.Label>
              <Form.Control
                style={{ height: "10rem" }}
                type="text"
                placeholder="Please type your address.."
              />
            </Form.Group>
            <Form.Group controlId="formBasicCheckbox" required>
              {" "}
            </Form.Group>
            <Button variant="primary" type="submit">
              Submits
            </Button>
          </Form>
        </div>
      </Modal>
    </div>
  );
};

export default AppointmentFrom;
