import React, { useState } from "react";
import { Button, Card, Form } from "react-bootstrap";
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';





const BookingCard = ({ booking, date }) => {

  const [open, setOpen] = useState(false);
 
  const onOpenModal = () => setOpen(true);
  const onCloseModal = () => setOpen(false);

  return (
    <div>
      
      <Card style={{width: '18rem', margin:"1rem", alignItems:"center"}}>
        <Card.Body>
          <Card.Title>{booking.subject}</Card.Title>
          <Card.Text>
            {booking.time}
          </Card.Text>
          <Card.Text>
          {booking.date}
          </Card.Text>
          <Card.Text>
          {booking.visitingHour}
          </Card.Text>
          <Card.Text>
          Total space available {booking.totalSpace}
          </Card.Text>
          <Button onClick={onOpenModal} variant="primary">Book Now</Button>
          <Modal open={open} onClose={onCloseModal} center>
          <div style={{ backgroundColor: "#3A4256", color:"white", padding:"1rem"}}>
      <Form className="container" style={{width:"30rem", alignItems:"centre"}}>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" />
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Label>Message</Form.Label>
          <Form.Control style={{height:"10rem"}} type="text" placeholder="Message" />
        </Form.Group>
        <Form.Group controlId="formBasicCheckbox"></Form.Group>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    </div>
        
      </Modal>
        </Card.Body>
      </Card>
     
    </div>
  );
};

export default BookingCard;
