import React, { useState } from 'react';
import Sidebar from './Sidebar/Sidebar';
import Calendars from '../../Component/MainAppoint/Calendar/Calendars';
import AppointmentsByDate from './AppointmentsByDate/AppointmentsByDate';
const Dashboard = () => {
   
    const [selectTheDates, setSelectTheDates] = useState(new Date());
    console.log(selectTheDates);
    const [appointment, setAppointment] = useState([]);

   const handleDateChange = date => {
        setSelectTheDates(date);
         fetch('http://localhost:5001/appointmentsByDate', {
        method: 'POST',
        headers: { 'content-type': 'application/json' },
        body: JSON.stringify({date})
      })
      .then(res => res.json())
      .then(data => setAppointment(data));
    }
    return (
        <section class="container-fluid">
            <div className="d-flex justify-content-center">
           <div className="col-md-2 col-sm-12">
           <Sidebar/>
           </div>
           <div className="col-md-5 col-sm-12">
           <Calendars onChange={handleDateChange} />
           </div>
           <div className="col-md-5 col-sm-12">
           <AppointmentsByDate appointment={appointment}/>
           </div>
        </div>
        </section>
    );
};

export default Dashboard;
