import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";

const FormField = () => {

  const [newInfo, setNewInfo] = useState({});

  const handleBlur = (e) => {
    const newData = { ...newInfo }
    newData[e.target.name] = [e.target.value]
    setNewInfo(newData);
    console.log(newData);
  }


  const handleReview = date => {
     fetch('http://localhost:5001/appointmentsByDate', {
    method: 'POST',
    headers: { 'content-type': 'application/json' },
    body: JSON.stringify({date: date.toDateString()})
  })
  .then(res => res.json())
  .then(data => setAppointment(data));
}

  return (
    <div style={{ backgroundColor: "#3A4256", color:"white", padding:"1rem"}}>
      <Form className="container" style={{width:"30rem", alignItems:"centre"}}>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" />
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Label>Message</Form.Label>
          <Form.Control style={{height:"10rem"}} type="text" placeholder="Message" />
        </Form.Group>
        <Form.Group controlId="formBasicCheckbox"></Form.Group>
        <Button onSubmit={handleReview} variant="primary">
          Submit
        </Button>
      </Form>
    </div>
  );
};

export default FormField;
