import React, { useState } from "react";
import { Button, Card, Form } from "react-bootstrap";
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';





const BookingCard = ({ booking, date }) => {

  const [open, setOpen] = useState(false);
 
  const onOpenModal = () => setOpen(true);
  const onCloseModal = () => setOpen();

  const onSubmit = data =>{

fetch(`http://localhost:5000/addAppointment`,{
    method: 'POST',
    headers: { 'Content-Type': 'application/json'},
    body: JSON.stringify(data)
    
  })
  .then(res => res.json())
  .then(success =>{
    if(success){
      onCloseModal();
      alert("Appointment created successfully!")
    }
  })
    
  }
  
 

  return (
    <div>
      
      <Card style={{width: '18rem', margin:"1rem", alignItems:"center"}}>
        <Card.Body>
          <Card.Title>{booking.subject}</Card.Title>
          <Card.Text>
            {booking.time}
          </Card.Text>
          <Card.Text>
          {booking.date}
          </Card.Text>
          <Card.Text>
          {booking.visitingHour}
          </Card.Text>
          <Card.Text>
          Total space available {booking.totalSpace}
          </Card.Text>
          <Button onClick={onOpenModal} variant="primary">Book Now</Button>
          
          
          
          <Modal open={open} center>
            <h3>Please provide your necessary information</h3>
          <div style={{ backgroundColor: "#3A4256", color:"white", padding:"1rem"}}>
      <Form className="container" style={{width:"30rem", alignItems:"centre"}}>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter email" required />
        </Form.Group>
        <div className="d-flex">
        <Form.Group controlId="formBasicEmail" required >
          <Form.Label>Name</Form.Label>
          <Form.Control type="text" placeholder="Enter your Name.." required/>
        </Form.Group>
        <Form.Group style={{marginLeft:"10px"}} controlId="formBasicEmail" required > 
          <Form.Label>Phone Number</Form.Label>
          <Form.Control type="text" placeholder="Enter your phone number" />
        </Form.Group>
        </div>

        <Form.Group controlId="formBasicPassword" required>
          <Form.Label>Address</Form.Label>
          <Form.Control style={{height:"10rem"}} type="text" placeholder="Please type your address.." />
        </Form.Group>
        <Form.Group controlId="formBasicCheckbox" required> </Form.Group>
        <Button onSubmit={onSubmit} variant="primary" type="submit">
          Submits
        </Button>
      </Form>
    </div>
        
      </Modal>
        </Card.Body>
      </Card>
     
    </div>
  );
};

export default BookingCard;
